	} while (strlen(p1->restaurant) == 0);
	for (p = list->head; p; p = p->next) //遍历链表
	{
		if (!(strcmp(p1->name, p->name) || strcmp(p1->restaurant, p->restaurant)))
		{
			color(12);
			printf("该饭店/食堂内已存在同名菜品！\n");
