	s_gets(a, SIZE1);
	printf("请输入菜品所属饭店名称：");
	s_gets(b, SIZE1);
	for (q = NULL, p = list->head; p; q = p, p = p->next)
	{														   //遍历链表
		if (!(strcmp(a, p->name) || strcmp(b, p->restaurant))) //判断是否找到要修改的节点
		{
			n++;   //记录找到了
			if (q) //判断找到的是不是第一个节点
			{
				q->next = p->next; //让该节点的前一个节点都next指向后一个节点
			}
			else
			{
				list->head = p->next; //让head的next指向后一个节点
			}
		}
	}
	if (!n)
	{
		printf("\n没有这道菜！\n");
	}
	else
	{
		printf("\n删除成功%d道菜。\n", n);
	}
	color(11);
