char *s_gets(char *m, int n) //安全输入字符
{
	char *p;
	char *find;
	p = fgets(m, n, stdin);
	if (p)
	{
		find = strchr(m, '\n'); //查找换行符
		if (find)				//如果地址不是NULL
			*find = '\0';		//在此处放一个空字符
		else
			while (getchar() != '\n')
				continue;
	}
	return p;
}
