	srand((unsigned)time(NULL) * 12);
	m = calculate(q);
	i = m;
	while (m > 0)
	{
		sum = sum * 10;
		m = m / 10;
	}
	while (1)
	{
		n = rand() % sum;
		if (n <= i && n > 0)
		{
			i = n;
			break;
		}
	}
	return i;
}
