int calculate(List *list) //è®¡ç®—å‡ºå•é“¾è¡¨çš„èŠ‚ç‚¹ä¸ªæ•°
{
	int n = 0;
	Node *p = list->head;
	while (p->next != NULL)
	{
		n++;
		p = p->next;
	}
	return n + 1;
}
void swap1(List *list, List *list_swap) //å•é“¾è¡¨å†’æ³¡æ’åºçš„ç®—æ³•å—1
{
	if (list_swap->pb)
	{
		list_swap->pb->next = list_swap->pa->next;
		list_swap->pa->next = list_swap->pc->next;
		list_swap->pc->next = list_swap->pa;
	}
	else
	{
		list->head = list_swap->pa->next;
		list_swap->pa->next = list_swap->pc->next;
		list_swap->pc->next = list_swap->pa;
	}
	list_swap->pb = list_swap->pc;
	list_swap->pc = list_swap->pa->next;
}
void swap2(List *list, List *list_swap) //å•é“¾è¡¨å†’æ³¡æ’åºçš„ç®—æ³•å—2
{
